package basicarray;

public class ArrayUse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//initially all elements in integer array is zero i.e, initialize with zero;
		int [] arrInt = new int[5];
		System.out.println(arrInt[0]);
		
		//initially all elements in character array is initialize with null values;
		char [] charArr = new char[5];
		System.out.println(charArr[0]);
		
		// initially all elements in double array is initialize with zero.zero values;
		double [] doubleArr = new double[5];
		System.out.println(doubleArr[0]);
		
		//initially all elements in boolean array is initialize all elements with boolean value "false";
		boolean arr[] = new boolean[5];
	    System.out.println(arr[0]);
	}

}
